sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	current.requests_today++;
	current.cache_discovery_request_count++;

	var apiKey = getApiKey();
	var srchType = current.googlemaps_request_type;

	try {
		var r = new sn_ws.RESTMessageV2('x_snc_trip_advisor.GoogleMaps', srchType);
		if (srchType == 'getDetails') {
			r.setStringParameterNoEscape('placeid', current.place_id);
			r.setStringParameterNoEscape('key', apiKey);
		} else {
			r.setStringParameterNoEscape('radius', current.radius);
			r.setStringParameterNoEscape('key', apiKey);
			r.setStringParameterNoEscape('location', current.location);
			r.setStringParameterNoEscape('type', current.type);	
		}

		//override authentication profile
		//authentication type ='basic'/ 'oauth2'
		//r.setAuthentication(authentication type, profile name);

		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
		gs.info('httpStatus = ' + httpStatus);
		gs.info('responseBody = ' + typeof responseBody);
		gs.info(responseBody);

		current.cache_discovery_last_update = gs.daysAgo(0);
		current.returned_raw_data = responseBody;
		current.cache_discovery_places_count = (responseBody.match(/place_id/g) || []).length;
		current.result = 'Success';
	}
	catch(ex) {
		var message = ex.getMessage();
		current.returned_raw_data = message;
		current.result = 'Failed';
	}



	current.update();

})(current, previous);

function getApiKey() {
	var Key = gs.getProperty("x_snc_trip_advisor.googlekey") + '';
	if (Key == "") {
		gs.addErrorMessage("There was no API key, please check the Google Key property");
		return;
	}
	return Key;
}
